"use client";

import { Support as CossistantSupport } from "@cossistant/react";
import { Bubble } from "./bubble";

export default function Support() {
  return <CossistantSupport slots={{ bubble: Bubble }} />;
}

"use client";

import { SupportProvider } from "@cossistant/next";

export function CossistantProvider({ children }: { children: React.ReactNode }) {
	return <SupportProvider>{children}</SupportProvider>;
}

"use client";

import { Bubble as PrimitiveBubble } from "@cossistant/react/primitives";
import { AnimatePresence, motion } from "motion/react";
import { ChevronDown, MessageCircle } from "lucide-react";

export function Bubble({ className }: { className?: string }) {
	return (
		<PrimitiveBubble asChild>
			{({ isOpen, unreadCount }) => (
				<motion.button
					type="button"
					className={`relative flex size-14 cursor-pointer items-center justify-center rounded-full bg-primary text-primary-foreground hover:bg-primary/90 ${className ?? ""}`}
					whileTap={{ scale: 0.95 }}
				>
					<AnimatePresence mode="wait">
						{isOpen ? (
							<motion.div
								key="close"
								initial={{ scale: 0.8, opacity: 0 }}
								animate={{ scale: 1, opacity: 1 }}
								exit={{ scale: 0.8, opacity: 0 }}
							>
								<ChevronDown className="size-5" />
							</motion.div>
						) : (
							<motion.div
								key="open"
								initial={{ scale: 0.8, opacity: 0 }}
								animate={{ scale: 1, opacity: 1 }}
								exit={{ scale: 0.8, opacity: 0 }}
							>
								<MessageCircle className="size-6" />
							</motion.div>
						)}
					</AnimatePresence>
					{unreadCount > 0 && (
						<span className="absolute -top-1 -right-1 flex size-5 items-center justify-center rounded-full bg-destructive text-xs text-destructive-foreground">
							{unreadCount}
						</span>
					)}
				</motion.button>
			)}
		</PrimitiveBubble>
	);
}
